export default function Cards({match}) {
  const [productos, setProductos] = React.useState([]);
  const classes = useStyles();
  const [expanded, setExpanded] = React.useState(false);

  const handleExpandClick = () => {
    setExpanded(!expanded);

  };

  React.useEffect(() =>{

    axios.get('/ws/rest/productos')
    .then(res => {
      const productos = res.data;
      // this.setState({ productos: res.data });
      setProductos(productos);
      // console.log(productos);
    })
    .catch(err => {
      console.log(err);
    });

  } );


  return (


      productos.map(p => {

    <Card className={classes.card}>
    <CardHeader
      action={
      <IconButton aria-label="settings">
        <Link to={`productos/edit/${producto.id}`}>
          <Edit>
            </Edit>
        </Link>
        </IconButton>
      }
      title="Prueba"
    />
    <CardMedia
      className={classes.media}
      image="https://ichef.bbci.co.uk/images/ic/640x360/p0659ssc.jpg"
    />
    <CardContent>
      <Typography variant="body2" color="textSecondary" component="p">
      <a>
Monta침a del Himalaya
      </a>
      </Typography>
    </CardContent>
    <CardActions disableSpacing>
      <IconButton aria-label="add to favorites">
        <FavoriteIcon/>
      </IconButton>
      <IconButton arial-label="Delete" onClick = "">
      <DeleteIcon/>
      </IconButton>
      <IconButton
        className={clsx(classes.expand, {
          [classes.expandOpen]: expanded,
        })}
        onClick={handleExpandClick}
        aria-expanded={expanded}
        aria-label="show more"
      >
        <ExpandMoreIcon />
      </IconButton>
    </CardActions>
    <Collapse in={expanded} timeout="auto" unmountOnExit>
      <CardContent>
        Descripcion del producto
      </CardContent>
    </Collapse>
  </Card>
      })


  );
}
